metas._ninja_tables_custom_css:
#footable_parent_NT_ID tr.footable-paging * { 
text-shadow: none !important;
}

.highlight_quote {
  color: #000000;
  font-weight: bold;
}

b {
    color: #000000;
}

.rowbold {
   color: #000000;
   font-weight: bold;
}


.listcontainer {
  text-align: center;
}

.myOL {
  display: inline-block;
  text-align: left;
}

table,
      th,
      td {
        padding: 10px;
        border: 1px solid black;
        border-collapse: collapse;
        table-layout: auto;
      }

blockquote {
  border-left: 10px solid #ccc;
  quotes: "\201C""\201D""\2018""\2019";
  text-align: center;
  margin: 1.5em 10px;
  padding: 0.5em 10px;
  color: #000000;
}
blockquote:before {
  color: #000000;
  content: open-quote;
  font-size: 4em;
  line-height: 0.1em;
  margin-right: 0.25em;
  vertical-align: -0.4em;
}
blockquote:after {
  color: #000000;
  font-size: 4em;
  line-height: 0.1em;
  margin-right: 0.25em;
  vertical-align: -0.4em;
}
blockquote p {
  display: inline;
}
